content.query:
let searchvalue = "{Variable}";
BehaviorAnalytics
| where TimeGenerated >= {Timerange:start} and TimeGenerated <= {Timerange:end}
| where isnotempty(searchvalue)
| where UserName contains_cs "{Variable}"
| summarize count() by ActionType

let ip_address = '{Variable}';
Syslog
| extend 
//    SerialNumber = extract("sn=([\\w\\d]+)", 1, SyslogMessage),
//    Firewall = extract("fw=([\\d\\.]+)", 1, SyslogMessage),
//    Priority = extract("pri=(\\d+)", 1, SyslogMessage),
    Message = extract("msg=\"([^\"]+)\"", 1, SyslogMessage),
//    SrcMac = extract("srcMac=([\\w\\d:]+)", 1, SyslogMessage),
    SrcIP = extract("src=([\\d\\.]+):", 1, SyslogMessage),
    SrcPort = extract("src=[\\d\\.]+:(\\d+)", 1, SyslogMessage),
//    NatSrc = extract("natSrc=([\\d\\.]+)", 1, SyslogMessage),
//    DstMac = extract("dstMac=([\\w\\d:]+)", 1, SyslogMessage),
    DstIP = extract("dst=([\\d\\.]+):", 1, SyslogMessage),
    DstPort = extract("dst=[\\d\\.]+:(\\d+)", 1, SyslogMessage),
    Protocol = extract("proto=([\\w\\d]+)\\/", 1, SyslogMessage),
    Application = extract("proto=[\\w\\d]+\\/([a-zA-Z]+)", 1, SyslogMessage),
    Category = extract("Category=\"([^\"]+)\"", 1, SyslogMessage),
    FirewallAction = extract("fw_action=\"([^\"]+)\"", 1, SyslogMessage)
 //   Code = extract("code=(\\d+)", 1, SyslogMessage)
| where SrcIP == ip_address or DstIP == ip_address 
| project TimeGenerated, Message, SrcIP, SrcPort, DstIP, DstPort, Protocol, Application, Category, FirewallAction
| order by TimeGenerated desc
